  <div class="container">
    <div class="text-center" style="margin-bottom: 18px;">
      <h1>القوانين</h1>
    </div>
    <div class="rulesBut row text-center">
      <div class="col-12 col-sm-6 col-md-3 mt-5">
        <button type="button" class="btn btn-primary rChoose active" data-class=".fundamental">العامه</button>
      </div>

